mapper.select:
SELECT *
FROM goal_history
WHERE goal_id = #{goalId}
  AND record_date = #{recordDate}

mapper.update:
UPDATE goal_history
SET progress_value = #{progressValue},
    is_completed = #{isCompleted}
WHERE id = #{id}

UPDATE goal_history
SET progress_value = #{progressValue},
    is_completed = #{isCompleted}
WHERE goal_id = #{goalId}
  AND record_date = #{recordDate}

UPDATE goal_history
SET title = #{title},
    target_value = #{targetValue},
    progress_value = #{progressValue},
    is_completed = #{isCompleted}
WHERE goal_id = #{goalId}
  AND record_date = #{recordDate}
